mapper.select:
select k.id, k.user_id, k.k_name, k.abstract, k.content, k.type, k.status, k.valid_time,k.checked_name, k.invalid_time, k.comment_count, k.collect_count, k.keywords,k.score, u.real_name as username from knowledge k, user u where status = 4 and k.user_id = u.id and k.is_deleted = 0
and (content like CONCAT('%',#{queryKey},'%') or keywords like CONCAT('%',#{queryKey},'%') or k_name like CONCAT('%',#{queryKey},'%'))
and u.dept_id = #{deptId}

mapper.update:
update knowledge set status = #{status}, valid_time = #{validTime}, invalid_time = #{invalidTime},
                     abstract = #{abstractInfo}, content = #{content},
                     keywords = #{keywords}, checked_name = #{checkedName}
where id = #{id}

update knowledge set status = #{status}

    ,k_name = #{kName}


    ,abstract = #{abstractInfo}


    ,content = #{content}


    ,keywords = #{keywords}


    ,err_info = #{errInfo}

where id = #{id}
